from django.contrib import adminfrom Testlink.models import *class TestProjectAdmin(admin.ModelAdmin):    '''    '''    list_display = ('project_id', 'project_name', 'tms_zone', )    ordering = ('project_id', 'project_name', 'tms_zone', )    search_fields = ['project_name']class TestPlanAdmin(admin.ModelAdmin):    '''    '''    list_display = ('plan_id',  'plan_name', 'project', )    ordering = ('plan_id',  'plan_name', 'project', )    search_fields = ['plan_name', 'project__project_name']class ProjectTestCaseAdmin(admin.ModelAdmin):    '''    '''    list_display = ('external_id', 'common_name', 'version', 'suite_name', 'suite_path', 'project')    ordering = ('external_id', 'common_name', 'version', 'suite_name', 'suite_path', 'project')    search_fields = ['common_name', 'suite_name', 'suite_path', 'project_name']class PlanTestCaseAdmin(admin.ModelAdmin):    '''    '''    list_display = ('tc_id', 'version', 'tcv_id', 'testcase', 'plan', )    ordering = ('tc_id', 'version', 'tcv_id', 'testcase', 'plan', )    search_fields = ['testcase__common_name', 'plan__plan_name']class TestBuildAdmin(admin.ModelAdmin):    '''    '''    list_display = ('build_id', 'build_name', 'plan', )    ordering = ('build_id', 'build_name', 'plan', )class TestRunMapAdmin(admin.ModelAdmin):    '''    '''    list_display = (        'get_testrun_id', 'get_plantestcase_tc_id',        'get_testrun_common_name', 'get_plantestcase_plan_name',        'get_testbuild_build_name', 'get_testrun_result',    )        search_fields = ['testrun__common_name']class ExecutionAdmin(admin.ModelAdmin):    '''    '''    list_display = (        'get_plantestcase_tc_id', 'exec_id', 'status', 'get_testrun_id',        'get_testbuild_build_name', 'get_plantestcase_plan_name', 'notes',    )    ordering = ('exec_id', 'status', 'notes')    search_fields = ['testrunmap__testrun__common_name', 'notes',]#    fieldsets = (#        (None, {#            'fields': ('exec_id', 'status')#        }),#        ('Execution details', {#            'classes': ('collapse',),#            'fields': ('get_testrun_id', 'get_plantestcase_tc_id',#                       'get_testbuild_build_id', 'execution_ts', 'tcversion_id',#                       'tcversion_number', 'notes')#        }),#    )admin.site.register(TestProject, TestProjectAdmin)admin.site.register(TestPlan, TestPlanAdmin)admin.site.register(ProjectTestCase, ProjectTestCaseAdmin)admin.site.register(PlanTestCase, PlanTestCaseAdmin)admin.site.register(TestBuild, TestBuildAdmin)admin.site.register(TestRunMap, TestRunMapAdmin)admin.site.register(Execution, ExecutionAdmin)